const aiResponse = $input.item.json.content.parts[0].text;
let parsed;
try {
  const cleanJson = aiResponse.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  parsed = JSON.parse(cleanJson);
} catch (error) {
  return { json: { success: false, error: "Failed to parse AI response", rawResponse: aiResponse } };
}

const webhookData = $('Webhook').item.json.body;
const currentMeta = $('Extract Meta').item.json;
const serpItems = $('Split Out').all();
const scrapedItems = $('Extract Competitor Meta').all();

// Build competitors with fallback logic
const competitors = serpItems.map((serpItem, i) => {
  const scraped = scrapedItems[i];

  // Check if scraping gave real data (not an error)
  const hasScrapedData = scraped && 
    !scraped.json.error && 
    scraped.json.titleTag;

  return {
    rank: i + 1,
    title: hasScrapedData ? scraped.json.titleTag : (serpItem.json.title || 'N/A'),
    description: hasScrapedData ? scraped.json.metaDesc : (serpItem.json.snippet || 'N/A'),
    url: serpItem.json.link || 'N/A',
    source: hasScrapedData ? 'scraped' : 'serpapi_fallback'
  };
});

return {
  json: {
    success: true,
    data: {
      url: webhookData.url,
      keyword: webhookData.keyword,
      current: {
        title: currentMeta.titleTag || 'No title found',
        description: currentMeta.metaDesc || 'No description found'
      },
      competitors,
      competitorInsight: parsed.competitors_analysis,
      optimizedVariations: parsed.variations.map(v => ({
        title: v.title,
        titleLength: v.title.length,
        description: v.description,
        descriptionLength: v.description.length,
        strategy: v.strategy,
        meetsRequirements: {
          titleLength: v.title.length >= 50 && v.title.length <= 60,
          descriptionLength: v.description.length >= 150 && v.description.length <= 160
        }
      }))
    }
  }
};

